DROP TABLE IF EXISTS `dt_article`;
CREATE TABLE `dt_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `channel_id` int(11) DEFAULT NULL,
  `category_id` int(11) DEFAULT NULL,
  `call_index` varchar(50) DEFAULT NULL,
  `title` varchar(100) DEFAULT NULL,
  `link_url` varchar(255) DEFAULT NULL,
  `img_url` varchar(255) DEFAULT NULL,
  `seo_title` varchar(255) DEFAULT NULL,
  `seo_keywords` varchar(255) DEFAULT NULL,
  `seo_description` varchar(255) DEFAULT NULL,
  `tags` longtext,
  `zhaiyao` varchar(255) DEFAULT NULL,
  `content` longtext,
  `sort_id` int(11) DEFAULT NULL,
  `click` int(11) DEFAULT NULL,
  `status` tinyint(4) DEFAULT NULL,
  `is_msg` tinyint(4) DEFAULT NULL,
  `is_top` tinyint(4) DEFAULT NULL,
  `is_red` tinyint(4) DEFAULT NULL,
  `is_hot` tinyint(4) DEFAULT NULL,
  `is_slide` tinyint(4) DEFAULT NULL,
  `is_sys` tinyint(4) DEFAULT NULL,
  `user_name` varchar(100) DEFAULT NULL,
  `add_time` datetime DEFAULT NULL,
  `update_time` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=102 DEFAULT CHARSET=utf8;

