INSERT INTO `dt_article` VALUES ('95', '7', '48', '', '惠普（HP）LaserJet 2035商用黑白激光打印机（黑色）', '', '/upload/201504/20/thumb_201504200242250674.jpg', '惠普（HP）LaserJet 2035商用黑白激光打印机（黑色）', '惠普,HP,LaserJet 2035,激光打印机', '惠普（HP）LaserJet 2035商用黑白激光打印机（黑色）', '', '全新hp laserjet p2035系列激光打印机既可令您尽享卓越的打印体验，又可提高办公生产率。此款打印机外形精巧、简单易用，且具备多种强大功能，以全力打造高端惠普激光打印体验。此外，它还可快速轻松输出专业办公文档，因此特别适用于中小型企业和大型企业环境内的小型工作组和…', '<table width=\"735\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<img width=\"735\" height=\"473\" class=\"gomeImgLoad\" src=\"http://img5.gomein.net.cn/image/prodimg/productDesc/descImg/201504/desc1284/1000016696/6.jpg\" />\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<table width=\"735\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n					<tbody>\r\n						<tr>\r\n							<td width=\"19\">\r\n								&nbsp;\r\n							</td>\r\n							<td width=\"700\" class=\"yha\">\r\n								全新hp laserjet p2035系列激光打印机既可令您尽享卓越的打印体验，又可提高办公生产率。此款打印机外形精巧、简单易用，且具备多种强大功能，以全力打造高端惠普激光打印体验。此外，它还可快速轻松输出专业办公文档，因此特别适用于中小型企业和大型企业环境内的小型工作组和高使用量个人用户。\r\n							</td>\r\n							<td width=\"16\">\r\n								&nbsp;\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<table width=\"735\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n					<tbody>\r\n						<tr>\r\n							<td width=\"23\">\r\n								&nbsp;\r\n							</td>\r\n							<td width=\"689\" class=\"bk\">\r\n								&nbsp;\r\n							</td>\r\n							<td width=\"23\">\r\n								&nbsp;\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<table width=\"735\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n					<tbody>\r\n						<tr>\r\n							<td width=\"24\">\r\n								&nbsp;\r\n							</td>\r\n							<td width=\"293\">\r\n								<img width=\"268\" height=\"228\" class=\"gomeImgLoad\" src=\"http://img6.gomein.net.cn/image/prodimg/productDesc/descImg/201504/desc1284/1000016696/2.jpg\" />\r\n							</td>\r\n							<td width=\"392\">\r\n								<span class=\"yy\">以独特的价值实现强大的办公生产率</span><br />\r\n<span class=\"yhb\">·尽享高达30 ppm (letter/a4)的快捷打印速度。<br />\r\n·采用惠普0秒预热技术，节能模式下仅8秒即可快速输出首页。<br />\r\n·10/100以太网/快速以太网联网功能可确保轻松共享打印资源。<br />\r\n·使用usb或并行端口，轻松连接打印机与计算机。<br />\r\n·250页进纸盒和50页多用途纸盒，减少纸张输入次数。<br />\r\n·266 mhz处理器和16 mb内存可确保快速处理复杂文件。 </span>\r\n							</td>\r\n							<td width=\"26\">\r\n								&nbsp;\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<table width=\"735\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n					<tbody>\r\n						<tr>\r\n							<td width=\"23\">\r\n								&nbsp;\r\n							</td>\r\n							<td width=\"689\" class=\"bk\">\r\n								&nbsp;\r\n							</td>\r\n							<td width=\"23\">\r\n								&nbsp;\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<table width=\"735\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n					<tbody>\r\n						<tr>\r\n							<td width=\"26\">\r\n								&nbsp;\r\n							</td>\r\n							<td width=\"407\">\r\n								<span class=\"yy\">线条流畅、节省空间的设计为您全力打造专业品质</span><br />\r\n<span class=\"yhb\">·高达600 x 600 dpi的分辨率，通过分辨率增强技术(ret)，打印质感文本和清晰图像的专业办公文档。<br />\r\n·借助惠普一体化硒鼓，体验品质如一的输出效果和出色易用性。</span>\r\n							</td>\r\n							<td width=\"20\">\r\n								&nbsp;\r\n							</td>\r\n							<td width=\"282\">\r\n								<img width=\"258\" height=\"242\" class=\"gomeImgLoad\" src=\"http://img5.gomein.net.cn/image/prodimg/productDesc/descImg/201504/desc1284/1000016696/3.jpg\" />\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<table width=\"735\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n					<tbody>\r\n						<tr>\r\n							<td width=\"23\">\r\n								&nbsp;\r\n							</td>\r\n							<td width=\"689\" class=\"bk\">\r\n								&nbsp;\r\n							</td>\r\n							<td width=\"23\">\r\n								&nbsp;\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<table width=\"735\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n					<tbody>\r\n						<tr>\r\n							<td width=\"25\">\r\n								&nbsp;\r\n							</td>\r\n							<td width=\"277\">\r\n								<img width=\"253\" height=\"238\" class=\"gomeImgLoad\" src=\"http://img6.gomein.net.cn/image/prodimg/productDesc/descImg/201504/desc1284/1000016696/4.jpg\" />\r\n							</td>\r\n							<td width=\"405\">\r\n								<span class=\"yy\">容易设置、使用和维护</span><br />\r\n<span class=\"yhb\">·利用直观的控制面板，轻松导航打印机功能。<br />\r\n·一个打印机正面检修门可确保无忧更换硒鼓。<br />\r\n·借助可hp easy printer care软件，高效管理2到20台惠普激光打印机，掌握打印机的状态，配置等信息。<br />\r\n·hp suresupply便于订购打印耗材，避免意外停机。<br />\r\n·外形精巧，可节省宝贵的桌面空间。</span>\r\n							</td>\r\n							<td width=\"28\">\r\n								&nbsp;\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<table width=\"735\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n					<tbody>\r\n						<tr>\r\n							<td width=\"23\">\r\n								&nbsp;\r\n							</td>\r\n							<td width=\"689\" class=\"bk\">\r\n								&nbsp;\r\n							</td>\r\n							<td width=\"23\">\r\n								&nbsp;\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<table width=\"735\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n					<tbody>\r\n						<tr>\r\n							<td width=\"26\">\r\n								&nbsp;\r\n							</td>\r\n							<td width=\"396\">\r\n								<span class=\"yy\">更多特性，带来更好打印体验</span><br />\r\n<span class=\"yhb\">·选择独特的打印机安静模式，尽享异常安静的操作体验。<br />\r\n·支持a6卡片等多种介质尺寸和类型，可轻松制作各类商业文档。</span>\r\n							</td>\r\n							<td width=\"23\">\r\n								&nbsp;\r\n							</td>\r\n							<td width=\"290\">\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', '99', '0', '0', '1', '0', '0', '0', '0', '1', 'admin', '2015-04-20 02:43:38', null);
INSERT INTO `dt_article` VALUES ('96', '7', '48', '', '惠普(HP) Officejet Pro 8610商用彩色喷墨一体机', '', '/upload/201504/20/thumb_201504200253026047.jpg', '惠普(HP) Officejet Pro 8610商用彩色喷墨一体机', '惠普,Officejet Pro 8610,彩色喷墨一体机', '专业彩色打印件的单页成本比激光打印机低 50%，从而在日常打印中节省更多打印成本。', '', '专业彩色打印件的单页成本比激光打印机低 50%，从而在日常打印中节省更多打印成本。无论您身在何处，均可通过任意移动设备进行打印。 借助惠普“云打印”技术，您现在可以随时随地，随心打印。使用智能复印解决方案，创建完美无瑕的副本，同时轻松打印双面身份证。专业彩色打印…', '<p>\r\n	专业彩色打印件的单页成本比激光打印机低 50%，从而在日常打印中节省更多打印成本。\r\n</p>\r\n<p>\r\n	无论您身在何处，均可通过任意移动设备进行打印。 借助惠普“云打印”技术，您现在可以随时随地，随心打印。\r\n</p>\r\n<p>\r\n	使用智能复印解决方案，创建完美无瑕的副本，同时轻松打印双面身份证。\r\n</p>\r\n<p>\r\n	专业彩色打印成本低于激光打印机，而且生产率高，可大幅推动业务发展。\r\n</p>\r\n<p>\r\n	适用墨盒：HP 950XL 黑色/青色/品红/黄色超大容量墨盒 CN045AA/CN046AA/CN047AA/CN048AA\r\n</p>\r\n<p align=\"center\">\r\n	&nbsp;<img alt=\"WW-CNPublicStore:/Product/RichContent/IPG/CM750A-Pro-8600-20120704-ver1.jpg\" src=\"http://shopping1.hp.com/is-bin/intershop.static/WFS/WW-CNPublicStore-Site/WW-CNPublicStore/zh_CN/Product/RichContent/IPG/CM750A-Pro-8600-20120704-ver1.jpg\" border=\"0\" /> \r\n</p>', '99', '0', '0', '1', '0', '0', '0', '0', '1', 'admin', '2015-04-20 02:53:06', null);
