INSERT INTO `dt_article` VALUES ('101', '7', '51', '', 'Shinco/新科 S2300 无线麦克风 无线手持话筒 双手麦 KTV 舞台', '', '/upload/201504/20/thumb_201504200333240710.jpg', 'Shinco/新科 S2300 无线麦克风 无线手持话筒 双手麦 KTV 舞台', 'Shinco,新科,S2300,无线麦克风,无线手持话筒,双手麦', '', '', 'Shinco/新科 S2300 无线麦克风 无线手持话筒 双手麦 KTV 舞台有买就有送，买麦克风买一送九（话筒海绵套2个，话筒防滑胶圈2个，电脑转接头1个，5号电池4只），不容错过，数量有限，送完为止！', '<img class=\"gomeImgLoad\" alt=\"\" src=\"http://img5.gomein.net.cn/image/bbcimg/productDesc/descImg/201410/desc04/A0003850992/2989713.jpg\" />', '99', '2', '0', '1', '0', '0', '0', '0', '1', 'admin', '2015-04-20 03:34:55', '2015-04-20 03:41:02');

-- ----------------------------
-- Table structure for dt_article_albums
-- ----------------------------
DROP TABLE IF EXISTS `dt_article_albums`;
CREATE TABLE `dt_article_albums` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `article_id` int(11) DEFAULT NULL,
  `thumb_path` varchar(255) DEFAULT NULL,
  `original_path` varchar(255) DEFAULT NULL,
  `remark` longtext,
  `add_time` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=81 DEFAULT CHARSET=utf8;

