DROP TABLE IF EXISTS `dt_article_attribute_field`;
CREATE TABLE `dt_article_attribute_field` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(100) DEFAULT NULL,
  `title` varchar(100) DEFAULT NULL,
  `control_type` varchar(50) DEFAULT NULL,
  `data_type` varchar(50) DEFAULT NULL,
  `data_length` int(11) DEFAULT NULL,
  `data_place` tinyint(4) DEFAULT NULL,
  `item_option` longtext,
  `default_value` longtext,
  `is_required` tinyint(4) DEFAULT NULL,
  `is_password` tinyint(4) DEFAULT NULL,
  `is_html` tinyint(4) DEFAULT NULL,
  `editor_type` tinyint(4) DEFAULT NULL,
  `valid_tip_msg` varchar(255) DEFAULT NULL,
  `valid_error_msg` varchar(255) DEFAULT NULL,
  `valid_pattern` longtext,
  `sort_id` int(11) DEFAULT NULL,
  `is_sys` tinyint(4) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;

