-- Records of dt_article_attribute_field
-- ----------------------------
INSERT INTO `dt_article_attribute_field` VALUES ('2', 'sub_title', '副标题', 'single-text', 'nvarchar(255)', '255', '0', null, null, '0', '0', '0', '0', '可为空，最多255个字符', null, 's0-255', '99', '1');
INSERT INTO `dt_article_attribute_field` VALUES ('3', 'source', '信息来源', 'single-text', 'nvarchar(100)', '100', '0', null, '本站', '0', '0', '0', '0', '非必填，最多50个字符', null, 's0-50', '100', '1');
INSERT INTO `dt_article_attribute_field` VALUES ('4', 'author', '文章作者', 'single-text', 'nvarchar(50)', '50', '0', null, null, '0', '0', '0', '0', '非必填，最多50个字符', null, 's0-50', '101', '1');
INSERT INTO `dt_article_attribute_field` VALUES ('5', 'goods_no', '商品货号', 'single-text', 'nvarchar(100)', '100', '0', null, null, '0', '0', '0', '0', '允许字母、下划线，100个字符内', null, 's0-100', '102', '1');
INSERT INTO `dt_article_attribute_field` VALUES ('6', 'stock_quantity', '库存数量', 'number', 'int', '0', '0', null, '0', '0', '0', '0', '0', '库存数量为0时显示缺货状态', null, 'n', '103', '1');
INSERT INTO `dt_article_attribute_field` VALUES ('7', 'market_price', '市场价格', 'number', 'decimal(9,2)', '0', '2', null, '0', '0', '0', '0', '0', '市场的参与价格，不参与计算', null, '/^(([1-9]{1}\\d*)|([0]{1}))(\\.(\\d){1,2})?$/', '104', '1');
INSERT INTO `dt_article_attribute_field` VALUES ('8', 'sell_price', '销售价格', 'number', 'decimal(9,2)', '0', '2', null, '0', '1', '0', '0', '0', '*出售的实际交易价格', null, '/^(([1-9]{1}\\d*)|([0]{1}))(\\.(\\d){1,2})?$/', '105', '1');
INSERT INTO `dt_article_attribute_field` VALUES ('9', 'point', '交易积分', 'number', 'int', '0', '0', null, '0', '0', '0', '0', '0', '*正为返还，负为消费积分', null, 'n', '106', '1');
INSERT INTO `dt_article_attribute_field` VALUES ('12', 'video_src', '视频上传', 'video', 'nvarchar(255)', '0', '0', null, null, '0', '0', '0', '0', null, null, null, '99', '0');

