-- ----------------------------
-- Table structure for dt_orders
-- ----------------------------
DROP TABLE IF EXISTS `dt_orders`;
CREATE TABLE `dt_orders` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `order_no` varchar(100) DEFAULT NULL,
  `trade_no` varchar(100) DEFAULT NULL,
  `user_id` int(11) DEFAULT NULL,
  `user_name` varchar(100) DEFAULT NULL,
  `payment_id` int(11) DEFAULT NULL,
  `payment_fee` decimal(19,2) DEFAULT NULL,
  `payment_status` tinyint(4) DEFAULT NULL,
  `payment_time` datetime DEFAULT NULL,
  `express_id` int(11) DEFAULT NULL,
  `express_no` varchar(100) DEFAULT NULL,
  `express_fee` decimal(19,2) DEFAULT NULL,
  `express_status` tinyint(4) DEFAULT NULL,
  `express_time` datetime DEFAULT NULL,
  `accept_name` varchar(50) DEFAULT NULL,
  `post_code` varchar(20) DEFAULT NULL,
  `telphone` varchar(30) DEFAULT NULL,
  `mobile` varchar(20) DEFAULT NULL,
  `email` varchar(30) DEFAULT NULL,
  `area` varchar(100) DEFAULT NULL,
  `address` longtext,
  `message` longtext,
  `remark` longtext,
  `is_invoice` tinyint(4) DEFAULT NULL,
  `invoice_title` varchar(100) DEFAULT NULL,
  `invoice_taxes` decimal(19,2) DEFAULT NULL,
  `payable_amount` decimal(19,2) DEFAULT NULL,
  `real_amount` decimal(19,2) DEFAULT NULL,
  `order_amount` decimal(19,2) DEFAULT NULL,
  `point` int(11) DEFAULT NULL,
  `status` tinyint(4) DEFAULT NULL,
  `add_time` datetime DEFAULT NULL,
  `confirm_time` datetime DEFAULT NULL,
  `complete_time` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

