DROP TABLE IF EXISTS `dt_order_goods`;
CREATE TABLE `dt_order_goods` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `article_id` int(11) DEFAULT NULL,
  `order_id` int(11) DEFAULT NULL,
  `goods_no` varchar(50) DEFAULT NULL,
  `goods_title` varchar(100) DEFAULT NULL,
  `img_url` varchar(255) DEFAULT NULL,
  `spec_text` longtext,
  `goods_price` decimal(19,2) DEFAULT NULL,
  `real_price` decimal(19,2) DEFAULT NULL,
  `quantity` int(11) DEFAULT NULL,
  `point` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

