-- ----------------------------
-- View structure for view_channel_content
-- ----------------------------
DROP VIEW IF EXISTS `view_channel_content`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `view_channel_content` AS SELECT dt_article.* FROM dt_article_attribute_value INNER JOIN dt_article ON dt_article_attribute_value.article_id = dt_article.id WHERE dt_article.channel_id=11 ;

-- ----------------------------
-- View structure for view_channel_down
-- ----------------------------
DROP VIEW IF EXISTS `view_channel_down`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `view_channel_down` AS SELECT dt_article.*,dt_article_attribute_value.source,dt_article_attribute_value.author FROM dt_article_attribute_value INNER JOIN dt_article ON dt_article_attribute_value.article_id = dt_article.id WHERE dt_article.channel_id=10 ;

-- ----------------------------
-- View structure for view_channel_goods
-- ----------------------------
DROP VIEW IF EXISTS `view_channel_goods`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `view_channel_goods` AS SELECT dt_article.*,dt_article_attribute_value.sub_title,dt_article_attribute_value.goods_no,dt_article_attribute_value.stock_quantity,dt_article_attribute_value.market_price,dt_article_attribute_value.sell_price,dt_article_attribute_value.point FROM dt_article_attribute_value INNER JOIN dt_article ON dt_article_attribute_value.article_id = dt_article.id WHERE dt_article.channel_id=7 ;

-- ----------------------------
-- View structure for view_channel_news
-- ----------------------------
DROP VIEW IF EXISTS `view_channel_news`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `view_channel_news` AS SELECT dt_article.*,dt_article_attribute_value.source,dt_article_attribute_value.author FROM dt_article_attribute_value INNER JOIN dt_article ON dt_article_attribute_value.article_id = dt_article.id WHERE dt_article.channel_id=6 ;

-- ----------------------------
-- View structure for view_channel_photo
-- ----------------------------
DROP VIEW IF EXISTS `view_channel_photo`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `view_channel_photo` AS SELECT dt_article.*,dt_article_attribute_value.source,dt_article_attribute_value.author FROM dt_article_attribute_value INNER JOIN dt_article ON dt_article_attribute_value.article_id = dt_article.id WHERE dt_article.channel_id=9 ;

-- ----------------------------
-- View structure for view_channel_video
-- ----------------------------
DROP VIEW IF EXISTS `view_channel_video`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost`  VIEW `view_channel_video` AS SELECT dt_article.*,dt_article_attribute_value.video_src,dt_article_attribute_value.sub_title FROM dt_article_attribute_value INNER JOIN dt_article ON dt_article_attribute_value.article_id = dt_article.id WHERE dt_article.channel_id=8 ;
